import { Component } from '@angular/core';

@Component({
  selector: 'app-contacts-page',
  templateUrl: './contacts-page.component.html',
  styleUrls: ['./contacts-page.component.sass']
})
export class ContactsPageComponent {

}
<!DOCTYPE html>
<html>

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>KПI ім. Ігоря Сікорського</title>
</head>
<style>
  html {
    line-height: 1;
    -webkit-text-size-adjust: 100%;
  }

  body {
    margin: 0;
    background-image: url(../../../assets/img/contacts-page-img/5.jpg);
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .main-container {
    display: flex;
    overflow-x: auto; /* Горизонтальна прокрутка */
    overflow-y: auto; /* Вертикальна прокрутка */
    max-height: 100vh; /* Встановіть фіксовану висоту */
  }

  .container {
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    max-height: 100%; /* Забезпечити, що контейнер не виходить за межі основного блоку */
  }

  .left {
    background: rgba(0, 0, 0, 0.43);
    border-radius: 10px;
    width: 560px;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    position: relative;
    max-height: 100%; /* Забезпечити, що контейнер не виходить за межі основного блоку */
  }

  .left_title {
    font-family: "Montserrat";
    font-size: 44px;
    line-height: normal;
    font-weight: 400;
    color: #ffffff;
    text-align: center;
    position: relative;
    margin-top: 20px;
    padding: 0 20px;
    max-width: 100%; /* Додайте цей рядок для обмеження ширини */
  }

  .contact-info img,
  .location-info img,
  .work_time img,
  .email img {
    width: 50px;
    margin-right: 30px;
    margin-left: 10px;
  }

  .call,
  .phone-number,
  .location,
  .loc,
  .work_time,
  .time,
  .email-text,
  .Email {
    max-width: 100%; /* Додайте цей рядок для обмеження ширини */
  }

  .left_title::after {
    content: "";
    display: block;
    height: 1px;
    width: 90%;
    background: #886F4F;
    border: 1px solid #886F4F;
    margin: 10px auto 0;
  }

  .right {
    background: rgba(0, 0, 0, 0.43);
    border-radius: 10px;
    width: 446px;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    position: relative;
    max-height: 100%; /* Забезпечити, що контейнер не виходить за межі основного блоку */
  }
</style>

<body>
<main class="main-container">
  <div class="container left">
    <p class="left_title">Зв'яжіться з нами</p>
    <div class="contact-info">
      <img src="../../../assets/img/contacts-page-img/icons_tel.jpg" alt="tel">
      <p class="call">Call Us</p>
    </div>
    <a class="phone-number" href="tel:+3800940939371">+3800940939371</a>
    <div class="location-info">
      <img src="../../../assets/img/contacts-page-img/icons_location.jpg" alt="location">
      <p class="location">Locations</p>
    </div>
    <p class="loc">Проспект Перемоги, 37, Київ, 03056</p>
    <div class="work_time">
      <img src="../../../assets/img/contacts-page-img/icons_clock.jpg" alt="clock">
      <p class="work_time">Work time</p>
    </div>
    <p class="time">Пн-Пт: 09:00 - 18:00<br>Сб-Нд: Зачинено</p>
    <div class="email">
      <img src="../../../assets/img/contacts-page-img/icons_email.jpg" alt="email">
      <p class="email-text">Email</p>
    </div>
    <a class="Email" href="mailto:">Mail@KPI.UA</a>
  </div>
  <div class="container right">

  </div>
</main>
<footer>
</footer>
</body>

</html>
