import { Component } from '@angular/core';

@Component({
  selector: 'app-contacts-page',
  templateUrl: './contacts-page.component.html',
  styleUrls: ['./contacts-page.component.sass']
})
export class ContactsPageComponent {

}
<!DOCTYPE html>
<html>

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>KPI ім. Ігоря Сікорського</title>
  <style>
    html {
      line-height: 1.15;
      -webkit-text-size-adjust: 100%;
    }

    body {
      display: block;
      margin: 0;
      background-image: url(../../../assets/img/contacts-page-img/5.jpg);
      background-repeat: no-repeat;
      background-position: center;
      background-size: cover;
    }

    .container {
      display: flex;
    }

    .container .left {
      flex: 1;
      background: rgba(0, 0, 0, 0.43);
      border-radius: 10px;
      width: 588px;
      height: 761px;
      margin-top: 142px;
      margin-left: 80px;
      display: block;
      flex-direction: column;
      justify-content: flex-start;
      align-items: center;
      position: relative;
    }

    .container .left_title {
      font-family: "Montserrat";
      font-size: 44px;
      line-height: normal;
      font-weight: 400;
      color: #ffffff;
      text-align: center;
      position: relative;
      margin-top: 40px;
      padding: 0 20px;
    }

    .container .contact-info {
      display: flex;
      align-items: center;
    }

    .container .contact-info img {
      width: 24px; /* Задайте розмір іконки, який вам підходить */
      margin-right: 10px; /* Відстань між іконкою і текстом */
    }

    .container .right {
      background: rgba(0, 0, 0, 0.43);
      flex: 1;
      border-radius: 10px;
      width: 466px;
      height: 513px;
      margin-top: 256px;
      margin-left: 80px;
      margin-right: 80px;
      display: block;
    }
  </style>
</head>

<body>
<main>
  <div class="container">
    <div class="left">
      <p class="left_title">Зв'яжіться з нами</p>
      <div class="contact-info">
        <img class="tel" src="../../../assets/img/contacts-page-img/icons_tel.jpg" alt="tel">
        <p>Call Us</p>
      </div>
    </div>
    <div class="right">
      <!-- Вміст другого контейнера -->
    </div>
  </div>
</main>
<footer>

</footer>
</body>

</html>
